dict.string:
# Abort if `seconds_to_lock` is unset or not an interger
if [[ -z "${seconds_to_lock}" || ! "${seconds_to_lock}" =~ ^[0-9]+$ ]]; then
  echo 'Lock time in the Workflow Environment Variables is not a full number' >&2
  exit 1
fi

[[ "${seconds_to_lock}" -le 0 ]] && exit 0

cat << EOB
  {
    "rerun" : 1,

    "variables": {
      "seconds_to_lock": "$(bc <<< "${seconds_to_lock} - 1")"
    },

    "items": [{
      "title": "MachineClean",
      "subtitle": "Unlocking in ${seconds_to_lock} seconds",
      "icon": "icon.png"
    }]
  }
EOB
