dict.string:
require 'json'

rerun_timer = ENV['rerun_timer'].empty? ? 3 : ENV['rerun_timer']
processes = %x(ps rcAo 'pid=,%cpu=,%mem=,nice=,command=').split("\n").map(&:split).map { |col| [col[0], col[1], col[2], col[3], col[4..-1].join(' ')] }

script_filter_items = []

processes.each do |item|
  pid = item[0]
  cpu = item[1]
  memory = item[2]
  nice = item[3]
  command = item[4]

  kill_exec = "kill -15 #{pid}"
  force_kill_exec = "kill -9 #{pid}"
  cpu_limit_exec = "cpulimit --include-children --limit #{ENV['default_cpu_limit']} --pid #{pid}"
  renice_exec = "renice -n #{ENV['default_renice']} -p #{pid}"
  freeze_exec = "kill -STOP #{pid}"

  script_filter_items.push(
    uid: pid,
    title: command,
    subtitle: "CPU: #{cpu}% / Memory: #{memory}% / Niceness: #{nice} / PID: #{pid}",
    arg: pid,
    mods: {
      cmd: {
        subtitle: "Kill process (#{kill_exec})",
        arg: kill_exec
      },
      alt: {
        subtitle: "Force kill process (#{force_kill_exec})",
        arg: force_kill_exec
      },
      ctrl: {
        subtitle: "Limit process CPU usage (#{cpu_limit_exec})",
        arg: cpu_limit_exec
      },
      fn: {
        subtitle: "Lower process priority (#{renice_exec})",
        arg: renice_exec
      },
      shift: {
        subtitle: "Freeze process (#{freeze_exec})",
        arg: freeze_exec
      }
    }
  )
end

puts({ rerun: rerun_timer, items: script_filter_items }.to_json)
