ObjC.import('stdlib');

let keep_alive_array = $.getenv('keep_alive').split(',').map(app => app.trim())
if ($.getenv('kill_finder') !== 'true') keep_alive_array.push('Finder')
if ($.getenv('kill_alfred') !== 'true') keep_alive_array.push('Alfred', 'Alfred Preferences')

const running_apps = Application('System Events').applicationProcesses.where({ backgroundOnly: false }).name().filter(app => !keep_alive_array.includes(app))

running_apps.forEach(app => Application(app).quit())

ObjC.import('stdlib');

let keep_alive_array = $.getenv('keep_alive').split(',').map(app => app.trim())
if ($.getenv('kill_finder') !== 'true') keep_alive_array.push('Finder')
if ($.getenv('kill_alfred') !== 'true') keep_alive_array.push('Alfred', 'Alfred Preferences')

const app = Application.currentApplication()
app.includeStandardAdditions = true

const quotedForm = (input) => "'" + input.toString().replace(/'/g, "'\\''") + "'"

const running_processes = Application('System Events').applicationProcesses.name().filter(processName => !keep_alive_array.includes(processName))

running_processes.forEach(processName => {
  try {
    app.doShellScript('killall ' + quotedForm(processName))
  } catch(error) {
    console.log('Skipping ' + processName)
  }
})
